@before_send = ->(event : Event, hint : Event::Hint | ::Nil) do
  (block.call(event, hint)).as(Event | ::Nil)
end

uri_path = uri.path.try(&.split('/'))
if uri.user
  @public_key = uri.user
  @secret_key = uri.password
  @project_id = uri_path.try(&.pop?).try(&.to_u64)
else
  @public_key = @secret_key = @project_id = nil
end
@scheme = uri.scheme
@host = uri.host
standard_ports = {http: 80, https: 443}
@port = uri.port
if scheme = @scheme
  if @port == standard_ports[scheme]?
    @port = nil
  end
end
@path = uri_path.try(&.join('/'))
if @path.try(&.empty?)
  @path = nil
end
@dsn = String.build do |str|
  (str << @scheme) << "://"
  if @public_key
    (str << @public_key) << '@'
  end
  str << @host
  if @port
    (str << ':') << @port
  end
  if @path
    str << @path
  end
  if @project_id
    (str << '/') << @project_id
  end
end

if message_or_ex.is_a?(Raven::Error)
else
  return false
end
@errors << "Refusing to capture Raven error: #{message_or_ex.inspect}"
true

